mapper.select:

select *
from (select /*+INDEX_DESC(myplace pk_myplace)*/ rownum rn,mno,userid,mcategory,nickname,mytown,title,content,regdate,updatedate,replycnt,curious
		from myplace where rownum <=(#{pageNum} * #{amount}))
where rn> (#{pageNum}-1) * #{amount}


select *
from (select /*+INDEX_DESC(myplace pk_myplace)*/ rownum rn,mno,userid,nickname,mytown,title,content,regdate,updatedate,curious,mcategory,replycnt
      from myplace where rownum <=(#{cri.pageNum} * #{cri.amount}) and mytown=#{mytown})
where rn> (#{cri.pageNum}-1) * #{cri.amount}


select *
from (select /*+INDEX_DESC(myplace pk_myplace)*/ rownum rn,mno,userid,nickname,mytown,title,content,regdate,updatedate,curious,mcategory,replycnt
		from myplace where rownum <=(#{cri.pageNum} * #{cri.amount}))
where rn> (#{cri.pageNum}-1) * #{cri.amount} and userid = #{userid}
