mapper.select:
select * from (select rownum rn,pno, price, title, good, puuid, puploadpath, pimgname, category, mytown , reservation from (select pd.pno, price, title, good, puuid, puploadpath, pimgname, category, mytown , reservation from product pd, (select * from PRODUCTIMG where Rowid in (select max(rowid) from PRODUCTIMG group by pno)) pdi where pd.pno = pdi.pno
and category = #{cate}
order by pd.pno desc) where rownum <= (#{pageNum} * #{amount})) where rn> (#{pageNum}-1) * #{amount}

select * from (select rownum rn,pno, price, title, good, puuid, puploadpath, pimgname, category, mytown , reservation from (select pd.pno, price, title, good, puuid, puploadpath, pimgname, category, mytown , reservation from product pd, (select * from PRODUCTIMG where Rowid in (select max(rowid) from PRODUCTIMG group by pno)) pdi where pd.pno = pdi.pno and pd.mytown = (select mytown from member where userid = #{userid})
and category = #{cri.cate}
order by pd.pno desc) where rownum <= (#{cri.pageNum} * #{cri.amount})) where rn> (#{cri.pageNum}-1) * #{cri.amount}
